import {GET_PROFILE, PROFILE_ERROR} from '../actions/types'

const initialState = {
  profile: null,
  profiles: [],
  repos: [],
  loading: true,
  error: {}
}

function switchProfileCase( state = initialState, action){

  switch(action.type){
    case GET_PROFILE:
    return {
      ...state,
      profile: payload,
      loading: false
    }
    case PROFILE_ERROR:
    return {
      ...state,
      error: payload,
      loading: false
    }
  }
}

export default switchProfileCase;
